import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
    const [usuarios, setUsuarios] = useState([]);
    const [nombre, setNombre] = useState("");
    const [correo, setCorreo] = useState("");

    // Obtener usuarios al cargar el componente
    useEffect(() => {
        obtenerUsuarios();
    }, []);

    // Función para obtener usuarios
    const obtenerUsuarios = () => {
        axios.get("http://localhost:8080/api/usuarios")
            .then(response => setUsuarios(response.data))
            .catch(error => console.error(error));
    };

    // Función para crear un nuevo usuario
    const crearUsuario = () => {
        axios.post("http://localhost:8080/api/usuarios", { nombre, correo })
            .then(response => {
                setUsuarios([...usuarios, response.data]);
                setNombre("");
                setCorreo("");
            })
            .catch(error => console.error(error));
    };

    // Función para eliminar un usuario

    const eliminarUsuario = (id) => {
        console.log('Eliminando usuario con ID:', id); // Verifica el ID que se va a eliminar
        axios.delete(`http://localhost:8080/api/usuarios/${id}`)
            .then((response) => {  // Asegúrate de que 'response' esté definido aquí
                console.log('Usuario eliminado', response.data); // Mensaje de confirmación
                setUsuarios(usuarios.filter(usuario => usuario.id !== id));
            })
            .catch(error => {
                // Imprimir información detallada del error
                if (error.response) {
                    console.error('Error al eliminar el usuario:', error.response.data);
                    alert(`Error al eliminar el usuario: ${error.response.data.error}`); // Mostrar el mensaje de error
                } else if (error.request) {
                    console.error('Error en la solicitud:', error.request);
                    alert('No se recibió respuesta del servidor.');
                } else {
                    console.error('Error:', error.message);
                    alert('Error desconocido al intentar eliminar el usuario.');
                }
            });
    };



    return (
        <div style={{ padding: "20px" }}>
            <h1>Lista de Usuarios</h1>
            {error && <p style={{ color: 'red' }}>{error}</p>} {/* Muestra mensaje de error */}
            <ul>
                {usuarios.map(usuario => (
                    <li key={usuario.id}>
                        {usuario.nombre} - {usuario.correo}
                        <button onClick={() => eliminarUsuario(usuarios[0].id)}>Eliminar Primer Usuario</button>

                    </li>
                ))}
            </ul>

            <h2>Crear Usuario</h2>
            <input
                type="text"
                placeholder="Nombre"
                value={nombre}
                onChange={(e) => setNombre(e.target.value)}
                style={{ marginRight: "10px" }}
            />
            <input
                type="correo"
                placeholder="correo"
                value={correo}
                onChange={(e) => setCorreo(e.target.value)}
                style={{ marginRight: "10px" }}
            />
            <button onClick={crearUsuario}>Agregar Usuario</button>
        </div>
    );
}

export default App;
