import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Swal from 'sweetalert2'; // Importar SweetAlert

function App() {
    const [usuarios, setUsuarios] = useState([]);
    const [nombre, setNombre] = useState("");
    const [email, setEmail] = useState("");

    // Obtener usuarios al cargar el componente
    useEffect(() => {
        obtenerUsuarios();
    }, []);

    // Función para obtener usuarios
    const obtenerUsuarios = () => {
        axios.get("http://localhost:8080/api/usuarios")
            .then(response => setUsuarios(response.data))
            .catch(error => console.error(error));
    };

    // Función para crear un nuevo usuario
    const crearUsuario = () => {
        axios.post("http://localhost:8080/api/usuarios", { nombre, correo: email })
            .then(response => {
                setUsuarios([...usuarios, response.data]);
                setNombre("");
                setEmail("");
                Swal.fire('Éxito', 'Usuario creado correctamente', 'success'); // Mensaje de éxito
            })
            .catch(error => Swal.fire('Error', 'No se pudo crear el usuario', 'error')); // Mensaje de error
    };

    // Función para eliminar un usuario
    const eliminarUsuario = (id) => {
        Swal.fire({
            title: '¿Estás seguro?',
            text: "¡No podrás revertir esto!",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Sí, eliminarlo!'
        }).then((result) => {
            if (result.isConfirmed) {
                axios.delete(`http://localhost:8080/api/usuarios/${id}`)
                    .then(() => {
                        setUsuarios(usuarios.filter(usuario => usuario.id !== id));
                        Swal.fire('Eliminado!', 'El usuario ha sido eliminado.', 'success'); // Mensaje de éxito al eliminar
                    })
                    .catch(error => Swal.fire('Error', 'Error al eliminar el usuario', 'error')); // Mensaje de error al eliminar
            }
        });
    };

    return (
        <div className="container" style={{ padding: "20px" }}>
            <h1 className="text-center">Lista de Usuarios</h1>
            <ul className="list-group">
                {usuarios.map(usuario => (
                    <li key={usuario.id} className="list-group-item d-flex justify-content-between align-items-center">
                        {usuario.nombre} - {usuario.correo}
                        <button className="btn btn-danger" onClick={() => eliminarUsuario(usuario.id)}>Eliminar</button>
                    </li>
                ))}
            </ul>

            <h2 className="mt-4">Crear Usuario</h2>
            <div className="input-group mb-3">
                <input
                    type="text"
                    className="form-control"
                    placeholder="Nombre"
                    value={nombre}
                    onChange={(e) => setNombre(e.target.value)}
                />
                <input
                    type="email"
                    className="form-control"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                />
                <button className="btn btn-primary" onClick={crearUsuario}>Agregar Usuario</button>
            </div>
        </div>
    );
}

export default App;
