import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
    const [usuarios, setUsuarios] = useState([]);
    const [nombre, setNombre] = useState("");
    const [correo, setCorreo] = useState("");

    // Obtener usuarios al cargar el componente
    useEffect(() => {
        obtenerUsuarios();
    }, []);

    // Función para obtener usuarios
    const obtenerUsuarios = () => {
        axios.get("http://localhost:8080/api/usuarios")
            .then(response => setUsuarios(response.data))
            .catch(error => console.error(error));
    };

    // Función para crear un nuevo usuario
    const crearUsuario = () => {
        axios.post("http://localhost:8080/api/usuarios", { nombre, correo })
            .then(response => {
                setUsuarios([...usuarios, response.data]);
                setNombre("");
                setCorreo("");
            })
            .catch(error => console.error(error));
    };

    // Función para eliminar un usuario
    const eliminarUsuario = (id) => {
        console.log('Eliminando usuario con ID:', id); // Verifica el ID que se va a eliminar
        axios.delete(`http://localhost:8080/api/usuarios/${id}`)
            .then(() => {
                setUsuarios(usuarios.filter(usuario => usuario.id !== id));
                console.log('Usuario eliminado'); // Mensaje de confirmación
            })
            .catch(error => {
                console.error('Error al eliminar el usuario:', error);
                alert('Error al eliminar el usuario. Por favor, intenta de nuevo.'); // Mensaje de alerta
            });
    };


    return (
        <div style={{ padding: "20px" }}>
            <h1>Lista de Usuarios</h1>
            <ul>
                {usuarios.map(usuario => (
                    <li key={usuario.id}>
                        {usuario.id} - {usuario.nombre} - {usuario.correo}
                        <button onClick={() => eliminarUsuario(usuario.id)} style={{ marginLeft: "10px" }}>Eliminar</button>
                    </li>
                ))}
            </ul>

            <h2>Crear Usuario</h2>
            <input
                type="text"
                placeholder="Nombre"
                value={nombre}
                onChange={(e) => setNombre(e.target.value)}
                style={{ marginRight: "10px" }}
            />
            <input
                type="correo"
                placeholder="correo"
                value={correo}
                onChange={(e) => setCorreo(e.target.value)}
                style={{ marginRight: "10px" }}
            />
            <button onClick={crearUsuario}>Agregar Usuario</button>
        </div>
    );
}

export default App;
