import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Swal from 'sweetalert2';

function App() {
    const [usuarios, setUsuarios] = useState([]);
    const [nombre, setNombre] = useState("");
    const [apellido, setApellido] = useState("");
    const [email, setEmail] = useState("");
    const [dni, setDni] = useState(""); // Nuevo estado
    const [direccion, setDireccion] = useState(""); // Nuevo estado
    const [usuarioActualizado, setUsuarioActualizado] = useState(null);

    useEffect(() => {
        obtenerUsuarios();
    }, []);

    const obtenerUsuarios = () => {
        axios.get("http://localhost:8080/api/usuarios")
            .then(response => setUsuarios(response.data))
            .catch(error => console.error(error));
    };

    const crearUsuario = () => {
        axios.post("http://localhost:8080/api/usuarios", { nombre, apellido, correo: email, dni, direccion })
            .then(response => {
                setUsuarios([...usuarios, response.data]);
                setNombre("");
                setApellido("");
                setEmail("");
                setDni(""); // Limpiar el campo de DNI
                setDireccion(""); // Limpiar el campo de dirección
                Swal.fire('Éxito', 'Usuario creado correctamente', 'success');
            })
            .catch(error => Swal.fire('Error', 'No se pudo crear el usuario', 'error'));
    };

    const eliminarUsuario = (id) => {
        Swal.fire({
            title: '¿Estás seguro?',
            text: "¡No podrás revertir esto!",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Sí, eliminarlo!'
        }).then((result) => {
            if (result.isConfirmed) {
                axios.delete(`http://localhost:8080/api/usuarios/${id}`)
                    .then(() => {
                        setUsuarios(usuarios.filter(usuario => usuario.id !== id));
                        Swal.fire('Eliminado!', 'El usuario ha sido eliminado.', 'success');
                    })
                    .catch(error => Swal.fire('Error', 'Error al eliminar el usuario', 'error'));
            }
        });
    };

    const editarUsuario = (usuario) => {
        setNombre(usuario.nombre);
        setApellido(usuario.apellido);
        setEmail(usuario.correo);
        setDni(usuario.dni); // Setear DNI
        setDireccion(usuario.direccion); // Setear dirección
        setUsuarioActualizado(usuario.id);
    };

    const actualizarUsuario = () => {
        if (usuarioActualizado) {
            axios.put(`http://localhost:8080/api/usuarios/${usuarioActualizado}`, { nombre, apellido, correo: email, dni, direccion })
                .then(response => {
                    const usuariosActualizados = usuarios.map(usuario => 
                        usuario.id === usuarioActualizado ? response.data : usuario
                    );
                    setUsuarios(usuariosActualizados);
                    setNombre("");
                    setApellido("");
                    setEmail("");
                    setDni(""); // Limpiar el campo de DNI
                    setDireccion(""); // Limpiar el campo de dirección
                    setUsuarioActualizado(null);
                    Swal.fire('Éxito', 'Usuario actualizado correctamente', 'success');
                })
                .catch(error => Swal.fire('Error', 'No se pudo actualizar el usuario', 'error'));
        }
    };

    return (
        <div className="container" style={{ padding: "20px" }}>
            <h1 className="text-center">Lista de Usuarios</h1>
            <ul className="list-group">
                {usuarios.map(usuario => (
                    <li key={usuario.id} className="list-group-item d-flex justify-content-between align-items-center">
                        {usuario.nombre} {usuario.apellido} - {usuario.correo} - {usuario.dni} - {usuario.direccion}
                        <div>
                            <button className="btn btn-info" onClick={() => editarUsuario(usuario)}>Editar</button>
                            <button className="btn btn-danger" onClick={() => eliminarUsuario(usuario.id)}>Eliminar</button>
                        </div>
                    </li>
                ))}
            </ul>

            <h2 className="mt-4">Crear / Actualizar Usuario</h2>
            <div className="input-group mb-3">
                <input
                    type="text"
                    className="form-control"
                    placeholder="Nombre"
                    value={nombre}
                    onChange={(e) => setNombre(e.target.value)}
                />
                <input
                    type="text"
                    className="form-control"
                    placeholder="Apellido"
                    value={apellido}
                    onChange={(e) => setApellido(e.target.value)}
                />
                <input
                    type="email"
                    className="form-control"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                />
                <input
                    type="text"
                    className="form-control"
                    placeholder="DNI" // Nuevo campo
                    value={dni}
                    onChange={(e) => setDni(e.target.value)}
                />
                <input
                    type="text"
                    className="form-control"
                    placeholder="Dirección" // Nuevo campo
                    value={direccion}
                    onChange={(e) => setDireccion(e.target.value)}
                />
                <button className="btn btn-primary" onClick={usuarioActualizado ? actualizarUsuario : crearUsuario}>
                    {usuarioActualizado ? 'Actualizar Usuario' : 'Agregar Usuario'}
                </button>
            </div>
        </div>
    );
}

export default App;
